      if (this.tipo == 9) {
        $("#divAlert").show();
        this.txtAlerta = "Debe seleccionar un tipo de contacto";
        $("#divAlert").fadeTo(2000, 500).slideUp(400, function () {
          $("divAlert").slideUp(500);
        });
      } else {
        if (this.tipo == 'DTCON2') {
          con = "CELULAR";
        } else if (this.tipo == 'DTCON1') {
          con = "TELEFONO";
        } else if (this.tipo == 'DTCON5') {
          con = "EMAIL PERSONAL";
        }

        this.descripcion = String($("#mdlDescripcion").val());
        this.operadora = Number($("#slcOperadora").val());

        if (this.operadora == 9) {
          $("#divAlert").show();
          this.txtAlerta = "Debe seleccionar un tipo de operadora";
          $("#divAlert").fadeTo(2000, 500).slideUp(400, function () {
            $("divAlert").slideUp(500);
          });
        } else {
          if (this.descripcion == "") {
            $("#divAlert").show();
            this.txtAlerta = "Debe ingresar una descripción";
            $("#divAlert").fadeTo(2000, 500).slideUp(400, function () {
              $("divAlert").slideUp(500);
            });
          } else {
            if (this.operadora == 1) {
              op = "CNT";
            } else if (this.operadora == 2) {
              op = "MOVISTAR";
            } else if (this.operadora == 3) {
              op = "CLARO";
            } else if (this.operadora == 4) {
              op = "OTROS";
            } else if (this.operadora == 5) {
              op = "FIJO";
            } else if (this.operadora == 0) {
              op = "";
            }

            if (this.check == true) {
              this.principal = 1;
            } else {
              this.principal = 0;
            }

            this.contactos.push({
              "DPCNT_CODIGO": "",
              "DPCNT_DESCRIPCION": this.descripcion,
              "DPCNT_OPERADORA": this.operadora,
              "DPCNT_OPERADORA_DET": op,
              "DPCNT_PRINCIPAL": this.principal,
              "DTCON_CODIGO": this.tipo,
              "DTCON_DESCRIPCION": con
            });
          }
        }
      }
