    descargar(codigo) {
      this.sisgerhService.obtenerAdjuntoInstruccion(codigo).subscribe(response => {
        if (response[0].ADJUNTO == null) {
          Swal.fire({
            icon: 'info',
            title: 'Información',
            text: 'No se encuentra un adjunto, actualizar el archivo'
          });
        } else {
          var bstr = atob(response[0].ADJUNTO),
              n = bstr.length,
              u8arr = new Uint8Array(n);

          while (n--) {
            u8arr[n] = bstr.charCodeAt(n);
          }

          let file = new File([u8arr], response[0].NOMBRE, {
            type: 'application/pdf'
          });
          var url = window.URL.createObjectURL(file);
          var anchor = document.createElement("a");
          anchor.download = response[0].NOMBRE;
          anchor.href = url;
          anchor.click();
        }
      });
