            pdf.add(new Table(tablasTotales).margin([475, 0, 0, 0]).headerRows(0).layout('headerLineOnly').widths([120, 90]).end);
            pdf.add(pdf.ln(1));
            //pdf.create().open();
            pdf.create().download('rol_pagos');
        }
    }
}
